// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

  /**
   * @title UserVault
   * @notice a personal asset vault for each user
   * @notice part of the StakeYourName DApp
   * @author Daniel Chilvers
   **/

import "@openzeppelin/contracts/proxy/Initializable.sol";

contract UserVault is Initializable{

    address private _owner;
    
    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    uint256 total;

    function initialize() public initializer{
        address msgSender = msg.sender;
        _owner = msgSender;
    }

    function doSomething(uint256 _input) public onlyOwner returns(uint256){
        total += _input;
        return(total);
    }

    function result() public view returns(uint256){
        return(total);
    }

}
